$(function(){

function DisableScroll()
{
	$('html, body').on('mousewheel', function(){
		return false;
	});
	$('html, body').on('keydown', function(){
		return false;
	});
	$('html, body').on('touchmove', function(){
		return false;
	});

	$('html, body').css({'overflow': 'hidden'});
}

function EnableScroll()
{
	$('html, body').off('mousewheel');
	$('html, body').off('keydown');
	$('html, body').off('touchmove');
	$('html, body').css({'overflow': 'auto'});
	$('body').css({'overflow-x': 'hidden'});
}

	$('a')
		.click(function (){
			elementClick = $('section[class*="'+this.href.split("#")[1]+'"]');
			destination = $(elementClick).offset().top;
			$("body,html").animate({scrollTop: destination }, 500);
		});

	$('.what-i-do-bar__red-button')
		.click(function (){
			destination = $('section[class*=bottom-block]').offset().top;
			$("body,html").animate({scrollTop: destination }, 500);
		});

	$('.img-bar__red-button_position')
		.click(function (){
			destination = $('section[class*=what-i-do-bar]').offset().top;
			$("body,html").animate({scrollTop: destination }, 500);
		});

	$('.menu-button')
		.click(function(){
			if($(this).hasClass('menu-button_disabled')){
				$(DisableScroll());
				$('.menu-button').removeClass('menu-button_disabled').addClass('menu-button_enabled');
				$('.menu-aside__bar').animate({left: '0'}, 300);
				$('.menu-aside').fadeIn();
			}
			else {
				$('.menu-button').removeClass('menu-button_enabled').addClass('menu-button_disabled');
				$('.menu-aside').fadeOut(200, EnableScroll);
				$('.menu-aside__bar').animate({left: '-85vw'}, 300);
			}
		});

	$('.menu-aside')
		.click(function(event){
			if(event.target == this){
				$('.menu-button').removeClass('menu-button_enabled').addClass('menu-button_disabled');
				$('.menu-aside').fadeOut(200, EnableScroll);
				$('.menu-aside__bar').animate({left: '-85vw'}, 300);
			}
			else {
				return false;
			}		
		});


	$('.menu-aside__title')
		.click(function(event){
			if(event.target == this){
				$('.menu-button').removeClass('menu-button_enabled').addClass('menu-button_disabled');
				$('.menu-aside').fadeOut(200, EnableScroll);
				$('.menu-aside__bar').animate({left: '-85vw'}, 300);
			}
			else {
				return false;
			}		
		});

	$('.call-order-button, .call-order-button-small, .examples__red-button_position')
		.click(function(event){
			if($('.menu-button').hasClass('menu-button_enabled')){
				$('.menu-button').removeClass('menu-button_enabled').addClass('menu-button_disabled');
				$('.menu-aside').fadeOut(200, EnableScroll);
				$('.menu-aside__bar').animate({left: '-85vw'}, 300);
			}
			$('.dark-bg').fadeIn(400).removeClass('dark-bg_disabled');
		});

	$('.close-button')
		.click(function(){
			$('.dark-bg').fadeOut(300).addClass('dark-bg_disabled');
		});

	$('.dark-bg')
	.click(function(){
			if(event.target == this){
				$('.dark-bg').fadeOut(300).addClass('dark-bg_disabled');
			}		
		});

});


$(document).ready(function(){
	$(".owl-carousel").owlCarousel({
		nav:true,
		navText:["<img src=\"img/arrow.png\">","<img src=\"img/arrow.png\">"],
		loop:true,
		margin:20,
		dots:false,
		mergeFit:false,
		responsive: {
			1024: {
				items: 3,
			},

			570: {
				items: 2,
				nav:false,
				dots:true,
				margin:30,
			},

			0: {
				items: 1,
				dots:true,
				nav:false,
				margin:0,
			}
		}
	});
});


